	public char findClass(String country)
	{
		for(int i=0;i<countryClassA.length;i++)
		{
			if(countryClassA[i].equals(country))
			{
				return 'A';
			}
		}
		for(int i=0;i<countryClassB.length;i++)
		{
			if(countryClassB[i].equals(country))
			{
				return 'B';
			}
		}
		for(int i=0;i<countryClassC.length;i++)
		{
			if(countryClassC[i].equals(country))
			{
				return 'C';
			}
		}
		return 'X';
	}
	
	public void processAllowance() {
		System.out.println("Country:"+travelRequest.getCountry());
		String grade=travelRequest.getEmployee().getGrade().trim();
		float dailyAllowance=0;
		if(localType.equals("international"))
		{
			switch(findClass(travelRequest.getCountry().trim()))
			{
				case 'A':	
				if(grade.equals("SM3"))
					dailyAllowance=80;	
				else if(grade.equals("SM2"))
					dailyAllowance=70;
				else if(grade.equals("SM1") || grade.equals("M3")|| grade.equals("M4"))
					dailyAllowance=60;
				else if(grade.equals("M2") || grade.equals("M1"))
					dailyAllowance=50;	
				break;
				
				case 'B':
				if(grade.equals("SM3"))
					dailyAllowance=55;	
				else if(grade.equals("SM2"))
					dailyAllowance=50;
				else if(grade.equals("SM1") || grade.equals("M3")|| grade.equals("M4"))
					dailyAllowance=45;
				else if(grade.equals("M2") || grade.equals("M1"))
					dailyAllowance=40;	
				break;
			
				case 'C':
				if(grade.equals("SM3"))
					dailyAllowance=50;	
				else if(grade.equals("SM2"))
					dailyAllowance=40;
				else if(grade.equals("SM1") || grade.equals("M3")|| grade.equals("M4"))
					dailyAllowance=35;
				else if(grade.equals("M2") || grade.equals("M1"))
					dailyAllowance=30;	
				break;
			}
		}
		
		//domestic
		else
		{
			if(grade.equals("SM3") || grade.equals("SM2") || grade.equals("SM1"))
				dailyAllowance=1000;	
			else if(grade.equals("M3") || grade.equals("M4"))
				dailyAllowance=800;
			else if(grade.equals("M1") || grade.equals("M2"))
				dailyAllowance=600;	
		}
		travelRequest.getTravelDetails().getAllowance().setDailyAllowance(dailyAllowance);
		this.computeDays();
		
	}
	public void setCurrency()
	{
		if(travelRequest.getCountry()==null)
			return;
		String country=travelRequest.getCountry();
		System.out.println("Country"+country+"assss");
		if(country.equals("USA"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("USD");
		else if(country.equals("UK"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("GBP");
		else if(country.equals("Japan"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("JPY");
		else if(country.equals("Europe"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("Euro");
		else if(country.equals("Singapore"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("SGD");
		else if(country.equals("China"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("CNY");
		else if(country.equals("India"))
			travelRequest.getTravelDetails().getAllowance().setCurrency("INR");
		else
			travelRequest.getTravelDetails().getAllowance().setCurrency("USD");
		this.processAllowance();
	}
	
